import { useQuery } from "@apollo/client";
import { getBookDetails } from "../queries/queries";

function BookDetails(props) {
    const {data, loading, error} = useQuery(getBookDetails, {variables: {id: props.bookId}});

    if(error || !data) return <p>Book not found!</p>;
    if(loading) return <p>Loading...</p>
    if(data){
        return (
            <div id="book-details">
              <p>{data.book.name}</p>
            </div>
          );
    };
}

export default BookDetails;
