import { useQuery, gql } from "@apollo/client";

const getBooksQuery = gql`
  {
    books {
      name
      genre
    }
  }
`;

function BookList() {
  const { loading, error, data } = useQuery(getBooksQuery);
  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error : {error.message}</p>;

  return (
    <ul id="book-list">
      <li>Book name</li>
    </ul>
  );
}

export default BookList;
